(287, 'Customs broker', 'customs-broker', 'customs-broker.jpg', '<p>Hoang Ha''s experienced, licensed customs brokers provide our customers hassle-free customs clearance services and professional solutions tailored to their specific logistics requirements.</p>\r\n', '<h2>Hoang Ha''s experienced, licensed customs brokers provide our customers hassle-free customs clearance services and professional solutions tailored to their specific logistics requirements.</h2>\r\n\r\n<p>We provides the fast, reliable and accurate customs brokerage service for shipments import and export</p>\r\n', '2013-09-19 15:55:56', '2013-09-19 15:11:21', 1, 'Customs broker', 1, 'en', ',7,', 'admin', 'admin', 0),
(288, 'Supply chain', 'supply-chain', 'supply-chain.jpg', '<p>At HIL, supply chain professionals help customers achieve overall supply chain savings, while gaining better control of their shipments.</p>\r\n', '<h2>At HIL, supply chain professionals help customers achieve overall supply chain savings, while gaining better control of their shipments.</h2>\r\n\r\n<p>Our experienced team can coordinate and optimize the logistical, financial and regulatory elements of your global supply chain.&nbsp;</p>\r\n', '2013-09-19 15:57:20', '2013-09-19 15:12:01', 1, 'Supply chain', 1, 'en', ',7,', 'admin', 'admin', 0),
(289, 'Imp exp documents', 'imp-exp-documents', 'imp-exp-documents.jpg', 'Imp exp documents: Health certificate, Phytosanitary certificate', '<p><img alt="intermodal" src="http://hoangha.com/images/stories/tintuc/31-07-2012/intermodal.jpg" style="font-size: 13px; float: right; margin-left: 10px; margin-right: 10px;" />Health certificate</p>\r\n\r\n<p>Phytosanitary certificate</p>\r\n', '2013-09-19 15:58:41', '2013-09-19 15:58:41', 1, 'Imp exp documents', 1, 'en', ',7,', 'admin', NULL, 0),
