(291, 'Packing services', 'packing-services', 'packing-services.jpg', '<p>HIL offers a full range of packing especially for seafood item packing such as fresh tuna, live crab, fresh fish etc.</p>\r\n', '<h2>HIL offers a full range of packing especially for seafood item packing such as fresh tuna, live crab, fresh fish etc.</h2>\r\n\r\n<p>Trained, professional packers are able to help you with as much or as little of the packing as you need in value added services . We also sell moving boxes and packing supplies.</p>\r\n', '2013-09-19 15:59:55', '2013-09-19 15:14:14', 1, 'Packing services', 1, 'en', ',7,', 'admin', 'admin', 0),
(292, 'Door to door services', 'door-to-door-services', 'door-services.jpg', '<p><span style="font-size: 13px;">HIL provides a global solution for moving time-sensitive documents and parcels door - to - door around the world and within a variety of transit time options that will meet your every need.</span></p>\r\n', '<h2>HIL provides a global solution for moving time-sensitive documents and parcels door - to - door around the world and within a variety of transit time options that will meet your every need.</h2>\r\n\r\n<p><strong>Our International Express services</strong> offer you real-time tracking information, Proof of Delivery of your shipments, and the ability to request delivery notification by Email or SMS.</p>\r\n', '2013-09-19 16:00:32', '2013-09-19 15:13:28', 1, 'Door to door services', 1, 'en', ',7,', 'admin', 'admin', 0),
(293, 'Warehouses', 'warehouses', 'warehouses.JPG', 'Located in the Saigon Port area, our facilities offer customers good access from and to all factories and ports. We offer warehouse rental for conventional cargoes of all kinds.', '<p>Located in the Saigon Port area, our facilities offer customers good access from and to all factories and ports. We offer warehouse rental for conventional cargoes of all kinds. Additional services are available: packing, labelling, crating, tallying...</p>\r\n\r\n<p style="text-align: center;"><img alt="Warehouses" src="http://hoangha.com/images/stories/tintuc/31-07-2012/Pic7_3.JPG" style="width: 250px; height: 168px;" /></p>\r\n', '2013-09-19 16:01:43', '2013-09-19 16:01:43', 1, 'Warehouses', 1, 'en', ',7,', 'admin', NULL, 0),
(294, 'Multimodal', 'multimodal', 'multimodal.jpg', 'HIL designs transport plans based on a network of approved suppliers, and then coordinates them with the involvement of its international teams. ', '<p><strong>HIL&nbsp;</strong>designs transport plans based on a network of approved suppliers, and then coordinates them with the involvement of its international teams. With air and ocean consolidation platforms in every major international hub, we are able to provide a broad range of groupage and logistics services.</p>\r\n\r\n<p style="text-align: center;"><img alt="Multimodal" src="http://hoangha.com/images/stories/tintuc/31-07-2012/multi.jpg" style="width: 200px; height: 123px;" /></p>\r\n', '2013-09-19 16:02:57', '2013-09-19 16:02:57', 1, 'Multimodal', 1, 'en', ',7,', 'admin', NULL, 0),
(295, 'CO2 - ICE PACK', 'co2-ice-pack', 'co2-ice-pack.jpg', 'The safest way to ship perishable items, such as food, is by packing them in dry ice. Dry ice is a solid form of carbon dioxide. It does not melt like frozen water, instead it sublimates-returns to its gaseous state.', '<h2>The safest way to ship perishable items, such as food, is by packing them in dry ice.</h2>\r\n\r\n<p>Dry ice is a solid form of carbon dioxide. It does not melt like frozen water, instead it sublimates-returns to its gaseous state.</p>\r\n\r\n<p>There is no water accumulation to damage your shipment when you use dry ice.</p>\r\n\r\n<p>Federal rules that govern all shipments containing dry ice require that you place special hazardous materials labeling on your package.</p>\r\n\r\n<p style="text-align: center;"><img alt="CO2 - ICE PACK" src="http://hoangha.com/images/stories/tintuc/31-07-2012/00367543.jpg" style="width: 334px; height: 500px;" /></p>\r\n', '2013-09-19 16:05:28', '2013-09-19 16:05:28', 1, 'CO2 - ICE PACK', 0, 'en', ',9,', 'admin', NULL, 0);
