INSERT INTO `web_article` (`id`, `name`, `name_alias`, `url`, `url_img`, `description`, `tags`, `content`, `other`, `datetime`, `lang`, `status`, `menu_id`) VALUES
(270, 'Our history Since 1992', 'our-history-since-1992', '', '', '<p>Our history Since 1992</p>\r\n', 'Our history Since 1992', '<table _mce_new="1" border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>1993</strong></td>\r\n			<td>- Vietnam Airlines Agent</td>\r\n			<td rowspan="22" style="vertical-align: top;"><img alt="HH 02" src="http://hoangha.com/images/stories/about_uss/HH_02.jpg" style="width: 300px; height: 200px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>- Foundation - the Preserving &amp; semi- processing warehouse for import - export goods.</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1994</strong></td>\r\n			<td>- Developing International Air Cargo service with International Airlines: TG, CX, SQ, BR...</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>- International Freight Forwarder</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1995</strong></td>\r\n			<td>- Top Agent of Vietnam Airline</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>- An official Agency is awarded the highest ranking of Vietnam Airlines.</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1996</strong></td>\r\n			<td>- Set up the carton factory and trucking team</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1997</strong></td>\r\n			<td>- Open the Airport Office 63 Truong Son St., HCMC</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1998</strong></td>\r\n			<td>- Top Agent of international Airlines: VN, TG, CX, OZ...</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1999</strong></td>\r\n			<td>- Member of IATA, FIATA, VIFFAS,VASEF</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>- Opening the HaNoi city branch</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2000</strong></td>\r\n			<td>- Established Da Nang Office</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2001</strong></td>\r\n			<td>- Established Hoang Ha Business Service Center in Tan Binh Industrial Zone</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2002</strong></td>\r\n			<td>- Set up the International Network</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2003</strong></td>\r\n			<td>- Set up the Frozen Factory</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2004</strong></td>\r\n			<td>- Top Agent of IATA members in Vietnam</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2005</strong></td>\r\n			<td>- Set up standard ISO: 9001 – 2000</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2006</strong></td>\r\n			<td>- Professionalize internation logistic</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2007</strong></td>\r\n			<td>- Supply chain management</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2008</strong></td>\r\n			<td>- Hoang Ha International Logistic J.S.C</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2009</strong></td>\r\n			<td>- Has been the winner of Vietnam Excellent Brand</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2011</strong></td>\r\n			<td>- Established Can Tho Office</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 1, 1376128663, 'en', 1, ',2,'),
(273, 'Hàng hóa dễ hỏng', 'hang-hoa-de-hong', '', 'perishables-cargo.jpg', 'Hàng hóa dễ hỏng. Chúng tôi cung cấp dịch vụ chuyên chở đặc biệt cho nhiều trường hợp. Quý khách hoàn toàn có thể tin tưởng vào sự quan tâm của HIL đối với từng kiện hàng đặc biệt.', 'Hàng hóa dễ hỏng', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;">CÁ TUNA</span></h2>\r\n\r\n			<p>Chúng tôi vận chuyển cá tươi Tuna từ làng đánh cá đến thị trường tiêu thụ</p>\r\n\r\n			<p>Với 1,500m2&nbsp;của khu vực đóng gói cá Tuna chúng tôi có thể cung cấp một hệ thống xử lý và đóng gói với tiêu chuẩn HACCP</p>\r\n			</td>\r\n			<td>\r\n			<h2><span style="color:#008000;"><img alt="CÁ TUNA" src="http://hoangha.com/images/stories/picture/IMG_5198%20n.jpg" style="width: 300px; height: 200px;" /></span></h2>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2">\r\n			<h2><span style="color:#008000;">RAU QUẢ TƯƠI</span></h2>\r\n\r\n			<p>Chúng tôi cung cấp dịch vụ chuyên chở đặc biệt cho nhiều trường hợp. Quý khách hoàn toàn có thể tin tưởng vào sự quan tâm của HIL đối với từng kiện hàng đặc biệt.</p>\r\n\r\n			<p>Xử lý và đóng gói tất cả các loai rau quả tươi</p>\r\n\r\n			<p>Với hệ thống kho lạnh chúng tôi cung cấp rau quả tươi vói chất lượng cao</p>\r\n\r\n			<p>Chúng tôi cung cấp dịch vụ hỗ trợ khai báo khai quan và chứng từ xuất khẩu</p>\r\n\r\n			<p>Chứng nhận bởi quốc tế</p>\r\n\r\n			<p style="text-align: center;"><img alt="RAU QUẢ TƯƠI" src="http://hoangha.com/images/stories/picture/quytrinh.jpg" style="width: 650px; height: 149px;" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;">HOA TƯƠI</span></h2>\r\n\r\n			<p>HIL chăm sóc cho bạn những bông hoa tươi đẹp.</p>\r\n\r\n			<p>Một số loài hoa khi gửi được đóng gói ngăn nắp trong các hộp chuyên dụng. Điều này cho phép số lượng lớn hoa được đóng gói trong không gian nhỏ như máy bay.</p>\r\n\r\n			<p>Có những loại hoa thì không thể giữ tươi trong một thời gian dài mà không có nước, vì thế khi vận chuyển người ta sẽ giữ một phần cuống hoa trong túi nước nhỏ.</p>\r\n			</td>\r\n			<td><img alt="HOA TƯƠI" src="http://hoangha.com/images/stories/picture/flowers_auction.jpg" style="width: 300px; height: 265px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;">THỰC PHẨM</span></h2>\r\n\r\n			<p>Với kinh nghiệm vận chuyển thực phẩm chúng tôi nhận thấy:</p>\r\n\r\n			<p>-&nbsp; Làm lạnh có thể làm giảm đáng kể tỷ lệ hư hỏng của thực phẩm tại một nhiệt độ nhất định.</p>\r\n\r\n			<p>-&nbsp; Nhiệt độ thấp làm chậm sự tăng trưởng của vi sinh vật, tỷ lệ hóa chất (bao gồm cả enzymic) thay đổi trong thực phẩm.</p>\r\n			</td>\r\n			<td><img alt="THỰC PHẨM" src="http://hoangha.com/images/stories/picture/food.jpg" style="width: 300px; height: 225px;" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 1376316971, 'vi', 0, ',18,'),
(274, 'Hàng hóa chuyên môn', 'hang-hoa-chuyen-mon', '', 'professional-cargo.jpg', 'Với HIL, vận chuyển hàng siêu trường siêu trọng là một thách thức mà chúng tôi đã vượt qua từ nhiều năm nay. Chúng tôi đã sẳn sàng đáp ứng bất cứ điều gì mà bạn yêu cầu vận chuyển bằng hàng không, không kể hàng hóa có kích thước hay cân nặng thế nào.', 'Hàng hóa chuyên môn', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;">OVERSIZED/ WEIGHT CARGO</span></h2>\r\n\r\n			<p>Vận chuyển hàng hóa siêu trường siêu trọng là một trong những hoạt động đặc trưng của công ty chúng tôi. Như vận chuyển các linh kiện máy bay, tua-bin, xe moto, cột điện.</p>\r\n\r\n			<p>Với HIL, vận chuyển hàng siêu trường siêu trọng là một thách thức mà chúng tôi đã vượt qua từ nhiều năm nay. Chúng tôi đã sẳn sàng đáp ứng bất cứ điều gì mà bạn yêu cầu vận chuyển bằng hàng không, không kể hàng hóa có kích thước hay cân nặng thế nào.</p>\r\n\r\n			<p>Nhân viên handling của chúng tôi hầu hết đều giàu kinh nghiệm và chuyên nghiệp, đặc biệt đối với hàng hóa có kích thước phức tạp và trọng lượng đặc biệt. Vì vậy khi nói đến hàng siêu trường siêu trọng, chúng tôi sẽ mang hàng của bạn đến tận nơi bạn cần. Hãy liên hệ với chúng tôi để cập nhật đầy đủ về hàng siêu trường siêu trọng.</p>\r\n			</td>\r\n			<td><img alt="OVERSIZED/ WEIGHT CARGO" src="http://hoangha.com/images/stories/about_uss/oversized-weight.jpg" style="width: 300px; height: 200px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;">DANGEROUS GOODS (DGR)</span></h2>\r\n\r\n			<p>Được trang bị hơn 10 nhân viên giàu kinh nghiệm với Chứng nhận của IATA về Hàng nguy hiểm – Dangerous Goods, cung cấp cho bạn giải pháp tốt nhất về những lô hàng DGR.</p>\r\n			</td>\r\n			<td><img alt="DANGEROUS GOODS (DGR)" src="http://hoangha.com/images/stories/picture/dangrerous.jpg" style="width: 300px; height: 274px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;">PHARMACEUTICALS</span></h2>\r\n\r\n			<p>Dược phẩm là một trong những thách thức cho ngành vận tải. Với dược phẩm, "giữ an toàn" mang nhiều ý nghĩa khác nhau. Một số sản phẩm có vòng đời rất ngắn, chỉ vài ngày hoặc vài tuần, một số khác có thể giữ lâu hơn nhưng chúng đều rất có giá trị cao vì vậy việc "giữ an toàn" là đặc biệt cần thiết.</p>\r\n\r\n			<p>Hãy đến với HIL sản phẩm của bạn sẽ được giữ an toàn một cách chuyên nghiệp.</p>\r\n			</td>\r\n			<td><img alt="PHARMACEUTICALS" src="http://hoangha.com/images/stories/about_uss/pharmaceuticals.jpg" style="width: 300px; height: 230px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;">DIAGNOTICS</span></h2>\r\n\r\n			<p>Máy chẩn đoán là mặt hàng thiết bị y tế được thiết kế để hỗ trợ trong việc giám sát, chẩn đoán hoặc điều trị các điều kiện y tế. Các thiết bị này thường được thiết kế với tiêu chuẩn an toàn nghiêm ngặt. Vận chuyển thiết bị này đòi hỏi khả năng chuyên môn và kiến thức. Nói chung thiết bị này là rất nhạy cảm và giá trị. Chỉ có Công ty Logistics chuyên nghiệp mới có thể thực hiện việc handle những mặt hàng này.</p>\r\n			</td>\r\n			<td><img alt="DIAGNOTICS" src="http://hoangha.com/images/stories/about_uss/Diagnotics.jpg" style="width: 300px; height: 118px;" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 1, 1376316982, 'vi', 0, ',18,'),
(259, 'About us home', 'about-us-home', '', 'gioi-thieu.jpg', '<p>Hoang Ha International Logistics (HIL) is established in 1992.</p>\r\n', 'About us home', '<h1>Hoang Ha International Logistics (HIL) is established in 1992.</h1>\r\n\r\n<h2>HIL is one of the world wide logistics management provider in Vietnam.</h2>\r\n\r\n<p>The range of services includes exports, imports, logistics customs brokerage, door to door delivery, air freight, sea freight, road transportation, warehouses and export&nbsp;documentation.</p>\r\n', 1, 1373889494, 'en', 1, ',1,'),
(271, 'Giới thiệu trang chủ', 'gioi-thieu-trang-chu', '', '', 'Công ty cổ phần quốc tế Lô-Gi-Stic Hoàng Hà (HIL) được thành lập từ năm 1992.\r\n', 'Giới thiệu trang chủ', '<h1>Công ty cổ phần quốc tế Lô-Gi-Stic Hoàng Hà (HIL) được thành lập từ năm 1992.</h1>\r\n\r\n<p>HIL là một trong những công ty cung cấp dịch vụ logistics sớm nhất ở Việt Nam.</p>\r\n\r\n<p>Dịch vụ của chúng tôi bao gồm: dịch vụ hàng không, dịch vụ tàu biển, đường bộ, xuất nhập khẩu, khai hải quan, chứng từ xuất nhập khẩu, giao hàng tận nơi, kho bãi.</p>\r\n\r\n<p>Dịch vụ vận chuyển hàng không HIL là lựa chọn tốt nhất cho những loại hàng hóa yêu cầu hạn định thời gian. Đội HIL Cargo sẽ thiết kế những giải pháp tình huống phù hợp nhất với nhu cầu của bạn.</p>\r\n', 1, 1376306760, 'vi', 1, ',39,'),
(278, 'AIR Service ', 'air-service', '', '', 'In air cargo service, Hoang Ha Int&#39;l Logistics has been continuously gained Top Agent of Airlines in many years: Vietnam Airline, Cathay Pacific Airway, Singapore Airline, China Airline, Thai Airway, Asiana Airline, Korean Air, Eva Airlines...', 'AIR Service ', '<h2>In air cargo service, Hoang Ha Int&#39;l Logistics has been continuously gained Top Agent of Airlines in many years: Vietnam Airline, Cathay Pacific Airway, Singapore Airline, China Airline, Thai Airway, Asiana Airline, Korean Air, Eva Airlines...</h2>\r\n\r\n<table _mce_new="1" border="0">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="TAM 1305 01" src="http://hoangha.com/images/stories/chungnhaita/TAM_1305_01.jpg" style="width: 185px; height: 278px;" /></td>\r\n			<td><img alt="TAM 1302 01" src="http://hoangha.com/images/stories/chungnhaita/TAM_1302_01.jpg" style="width: 185px; height: 278px;" /></td>\r\n			<td><img alt="TAM 1190 01" src="http://hoangha.com/images/stories/chungnhaita/TAM_1190_01.jpg" style="width: 185px; height: 278px;" /></td>\r\n			<td><img alt="TAM 1187 01" src="http://hoangha.com/images/stories/chungnhaita/TAM_1187_01.jpg" style="width: 185px; height: 278px;" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>8 IATA Certificates for Dangerous Goods.</p>\r\n\r\n<p>5 IATA Certificates for Cargo Marketing.</p>\r\n\r\n<p>10 IATA Certificate for Cargo Introductory.</p>\r\n\r\n<p>8 IATA Certificates for Cargo rating.</p>\r\n\r\n<p>5 Certificates for Customer Service..</p>\r\n\r\n<p>Big or small, all the shipments are equally important to us. Once the shipment orders have been confirmed, customers may rest assured that we will take the load off their mind. Their valuable cargo are well taken care of, from beginning to end. Our below services are just a phone call away from you:</p>\r\n\r\n<p>1. Airfreight forwarding</p>\r\n\r\n<p>2. Door-to-door delivery</p>\r\n\r\n<p>3. Customs clearance</p>\r\n\r\n<p>4. Insurance coverage arrangement</p>\r\n\r\n<p>5. Air consolidation export</p>\r\n\r\n<p>6. Air inbound deconsolidation</p>\r\n\r\n<p>7. Cargo survey arrangements</p>\r\n\r\n<p><strong>AIR IMPORT</strong></p>\r\n\r\n<p>Air import consolidation and Direct Import including: Dangerous Goods, Over-Dimensional Cargo, Perishable Cargo, Door-to-Door services including documenttation, Warehousing and Inland transportation..</p>\r\n\r\n<p><strong>AIR EXPORT</strong></p>\r\n\r\n<p>We specialize in cargoes that need special care or procedures such as: Live animals, perishables cargo, dangerous goods, heavy-oversized cargo...</p>\r\n\r\n<p><strong>HOANG HA</strong> cargo&#39;s reputation for expertise in handling of special shipments is throught our effcient customs procedures on time performance and ability to deiliver and store packing with special requirements.</p>\r\n', 0, 1379596460, 'en', 0, ',5,'),
(272, 'Lịch sử phát triển HIL', 'lich-su-phat-trien', '', '', '<p>Thành lập Công ty Cổ phần Quốc tế Logistics Hoàng Hà Giấy phép số 711/GP-UB TP. Thành lập kho bảo quản và sơ chế hàng hóa xuất nhập khẩu.</p>\r\n', 'Lịch sử phát triển Hoàng Hà,HIL từ năm 1992', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>1992</strong></td>\r\n			<td>- Thành lập Công ty TNHH Thương mại Hoàng Hà Giấy phép số 711/GP-UB TP</td>\r\n			<td rowspan="22" style="vertical-align: top;">\r\n			<p><img alt="HH 02" src="http://hoangha.com/images/stories/about_uss/HH_02.jpg" style="width: 400px; height: 266px;" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1993</strong></td>\r\n			<td>- Thành lập kho bảo quản và sơ chế hàng hóa xuất nhập khẩu.</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1994</strong></td>\r\n			<td>- Phát triển ngành vận tải hàng không với nhiều hãng bay: Việt Nam Airlines, TG, CX, SQ, BR .</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>- Trở thành đại lý giao nhận quốc tế.</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1995</strong></td>\r\n			<td>- Đại lý hàng đầu Việt Nam Airlines</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>- Vinh dự nhận bằng khen hạng I của Việt Nam Airlines</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1996</strong></td>\r\n			<td>- Lập xưởng bao bì và đội xe vận tải</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1997</strong></td>\r\n			<td>- Lập văn phòng sân bay tại 63 Trường Sơn, phường 2, quận Tân Bình, TP. Hồ Chí Minh</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1998</strong></td>\r\n			<td>- Đại lý hàng đầu các hãng hàng không quốc tế</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>1999</strong></td>\r\n			<td>- Thành viên IATA, FIATA, VIFFAS,VASEF</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>- Thành lập văn phòng tại Hà Nội</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>2000</strong></td>\r\n			<td>- Thành lập văn phòng tại Đà Nẵng</td>\r\n		</tr>\r